INSERT INTO `wp_postmeta` VALUES (948,153,'layouts_0_text1','We have designed a chair for the future of living space, where the place of life and work overlap.');
INSERT INTO `wp_postmeta` VALUES (949,153,'layouts_1_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (950,153,'layouts_1_text1','As remote work has become the norm, there are more and more occasionsfor people to work at home, in their living place. The traditional idea of separating work and home is changing, and the home is becoming a new place where relaxation and workoverlap.\r\n\r\nAs a result, people need furnitures that have a function andappearance appropriate to the new environment, however it is not efficient to place differentfurniture for different purposesin a room of limited size.');
INSERT INTO `wp_postmeta` VALUES (951,153,'layouts_2_layout_type','l-image-3column:画像3カラム ■■■');
