INSERT INTO `wp_postmeta` VALUES (955,153,'layouts_3_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (956,153,'layouts_3_text1','The \"switch chair\" was created in light of these changes in society.\r\n\r\nThe \"switch chair\" is a minimalist chair designed for a space where work and home are combined, where one chair can be used for both living and working.The “desk chair style”, straightens the back and makesit easy to concentrate on work.\r\n\r\nThe “rockingchair style”, gently lowers the seat backward and creates a deeply relaxed posture. The chair can be switched to the suitable sitting style for each environment.');
INSERT INTO `wp_postmeta` VALUES (957,153,'layouts_4_layout_type','l-image1:画像のみ1カラム（通常サイズ）■□');
INSERT INTO `wp_postmeta` VALUES (958,153,'layouts_4_image1','135');
