INSERT INTO `wp_postmeta` VALUES (1524,130,'layouts_0_title','生活の場と仕事の場が重なり合う、これからの住空間のための椅子をデザインしました。');
INSERT INTO `wp_postmeta` VALUES (1525,130,'_layouts_0_title','field_60db4b44ae2f4');
INSERT INTO `wp_postmeta` VALUES (1526,130,'layouts_1_image1','132');
INSERT INTO `wp_postmeta` VALUES (1527,130,'_layouts_1_image1','field_60db00c8815fb');
INSERT INTO `wp_postmeta` VALUES (1528,130,'layouts_1_image2','133');
