INSERT INTO `wp_postmeta` VALUES (1547,130,'_layouts_7_image3','field_60db4968ae2f2');
INSERT INTO `wp_postmeta` VALUES (1548,130,'layouts_8_text1','quantumでは社会やテクノロジーの変化に伴う、新しいプロダクトの在り方を提案するデザインリサーチプロジェクトを行っています。switch chairはそのプロジェクトの一環として製作されました。');
