INSERT INTO `wp_postmeta` VALUES (1733,240,'outline','Designing an autonomous catering robot that applies Nissan\'s driver assistance technology');
INSERT INTO `wp_postmeta` VALUES (1734,240,'clients_partners','0');
INSERT INTO `wp_postmeta` VALUES (1735,240,'layouts_0_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (1736,240,'layouts_0_text1','quantum was in charge of the design of the ProPILOT Waiter, an autonomous serving robot that applies Nissan\'s ProPILOTdriver assistance technology to automatically deliver food from the kitchen to the café table without human intervention.\r\n\r\nIn the summer of 2020, the ProPILOT waiter was used in the <a href=\"http://www.nissan.co.jp/BRAND/PAVILION/CAFE/CONCEPT-TECHNOLOGY/\" target=\"blank\">NISSAN CHAYA CAFE</a>, a café built alongside the <a href=\"https://www.nissan.co.jp/BRAND/PAVILION/\" target=\"blank\">Nissan Pavilion</a>, which opened for a limited time in Yokohama Minato Mirai 21, where people can experience Nissan\'s vision of the future of mobility throughvarious exhibits. It attracted a great deal of attention as a demonstration of the potential non-contact catering services especially when keeping social distance and minimizing human contact is recommended during this time.\r\n\r\n');
INSERT INTO `wp_postmeta` VALUES (1737,240,'layouts_1_layout_type','l-fullimage:画像ヨコイチ■');
INSERT INTO `wp_postmeta` VALUES (1738,240,'layouts_1_image1','225');
INSERT INTO `wp_postmeta` VALUES (1739,240,'layouts_2_layout_type','l-image-2column:画像2カラム ■■');
INSERT INTO `wp_postmeta` VALUES (1740,240,'layouts_2_image1','226');
INSERT INTO `wp_postmeta` VALUES (1741,240,'layouts_2_image2','227');
INSERT INTO `wp_postmeta` VALUES (1742,240,'layouts_3_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (1743,240,'layouts_3_text1','The product was designed based on the concept of \"nature and technology”. The table, based on the motif of a drop of water, consists of a table top witha form that gives the impression of the soft surface tension of water and a single thin leg.');
INSERT INTO `wp_postmeta` VALUES (1744,240,'layouts_4_layout_type','l-image-3column:画像3カラム ■■■');
INSERT INTO `wp_postmeta` VALUES (1745,240,'layouts_4_image1','228');
INSERT INTO `wp_postmeta` VALUES (1746,240,'layouts_4_image2','229');
