INSERT INTO `wp_postmeta` VALUES (1843,241,'_layouts_0_layout_type','field_60daf596a2cfb');
INSERT INTO `wp_postmeta` VALUES (1844,241,'layouts_0_text1','様々な障がいを持つ選手が集まるスポーツ・パラ卓球。選手同士は、同じ競技に挑んでいても違った困難を乗り越えて戦っています。そうしたひとりひとりのチャレンジを、インタビューやスケッチをもとに再現したのが「PARA PINGPONG TABLE」です。\r\n\r\n\r\n日本肢体不自由者卓球協会（以下、パラ卓球協会）のリブランディングプロジェクトとして、パラ卓球協会、三英、TBWA\\HAKUHODO、quantumの4社で共同開発。quantumは、プロダクトデザインを担当しました。\r\n');
INSERT INTO `wp_postmeta` VALUES (1845,241,'_layouts_0_text1','field_60db1bdce269b');
