INSERT INTO `wp_postmeta` VALUES (1847,241,'_layouts_1_layout_type','field_60daf596a2cfb');
INSERT INTO `wp_postmeta` VALUES (1848,241,'layouts_1_text1','パラ卓球選手は、抱える障がいの違いによって同じ長方形の卓球台でも違ったカタチに感じています。\r\n');
INSERT INTO `wp_postmeta` VALUES (1849,241,'_layouts_1_text1','field_60db1bdce269b');
