INSERT INTO `wp_postmeta` VALUES (1851,241,'_layouts_2_layout_type','field_60daf596a2cfb');
INSERT INTO `wp_postmeta` VALUES (1852,241,'layouts_2_text1','例えば車椅子の選手は、手の届く範囲に限界があるため、ネット際のボールはかなり遠く、卓球台をとても長く感じています。左足のつま先に力が入らない選手は、左ステップの踏ん張りが利かず、卓球台の左サイドをとても遠く感じます。\r\n\r\n');
