INSERT INTO `wp_postmeta` VALUES (1964,250,'layouts_4_image2','245');
INSERT INTO `wp_postmeta` VALUES (1965,250,'layouts_5_text1','The  PARA  PINGPONG  TABLE  recreates  the  world  of  pingpong  as  seen  by  such  paralympictable tennis players.When  viewed  from  the  side,  you  can  see  that  the  base,  which  has  the  same proportions  as  a standard pingpong table, gradually transforms into a unique shape of the tabletop. It is designed to allow for an intuitive visual understanding of the concept that “the shape perceived by each player is different”.');
INSERT INTO `wp_postmeta` VALUES (1966,250,'layouts_6_image1','246');
INSERT INTO `wp_postmeta` VALUES (1967,250,'layouts_7_text1','The  product  is  made  with  the  same  precision  as  a  tournament  grade  pingpong  table.  The  top board  is  made  of  \"Les  yeux  bleus,\"  the  same  material  as  the  official  table  for  international tournaments. The tabletop is made as flat as possible with minimal curvature, ensuring a uniform bounce along the entire surface.');
