INSERT INTO `wp_postmeta` VALUES (2134,261,'outline','Since the launch of the program in September 2018, we have been fully supporting East Japan Railway Company\'s (JR East) ON1000, a program to create new businesses within the group.');
INSERT INTO `wp_postmeta` VALUES (2135,261,'clients_partners','0');
INSERT INTO `wp_postmeta` VALUES (2136,261,'layouts_0_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2137,261,'layouts_0_text1','ON1000 is a new business creation program that aims to discover and commercialize \"discontinuous\" businesses that are not an extension of the JR East Group\'s existing businesses. It was named \"ON1000\" with the hope that many businesses (1000 ideas, 100 new businesses) will be created one after another.');
INSERT INTO `wp_postmeta` VALUES (2138,261,'layouts_1_layout_type','l-image1:画像のみ1カラム（通常サイズ）■□');
INSERT INTO `wp_postmeta` VALUES (2139,261,'layouts_1_text1','東日本旅客鉄道株式会社（JR東日本）による、グループ内新事業創造プログラム「ON1000」を、2018年9月のプログラム始動以来、全面的に支援しています。\r\n\r\n「ON1000」は、JR 東日本グループの既存事業の延長線上でない“非連続”な事業の発掘と事業化を目的とした新事業創造プログラムです。数多くの事業（1000 のアイデア、100 の新事業）が次々と生み出されるように、という想いを込めて「ON1000（オンセン）』と名付けられました。');
INSERT INTO `wp_postmeta` VALUES (2140,261,'layouts_2_layout_type','l-text-2column:テキストのみ1カラム □■');
