INSERT INTO `wp_postmeta` VALUES (2187,262,'outline','Creating the future of veterinary medicinethrough the eyesof pets and their families. QAL startups is a start-up studio that specializes in animal health related fields.');
INSERT INTO `wp_postmeta` VALUES (2188,262,'clients_partners','1');
