INSERT INTO `wp_postmeta` VALUES (2355,280,'layouts_4_text1','また、サポート時に手を添える部分にイエローのアクセントカラーを取り入れ、クルマイスの扱いに慣れていない人でも直感的に操作できるようなデザインを採用しています。');
INSERT INTO `wp_postmeta` VALUES (2356,280,'layouts_5_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2357,280,'layouts_5_image1','268');
INSERT INTO `wp_postmeta` VALUES (2358,280,'layouts_5_image2','269');
INSERT INTO `wp_postmeta` VALUES (2359,280,'layouts_6_layout_type','l-image-2column:画像2カラム ■■');
INSERT INTO `wp_postmeta` VALUES (2360,280,'layouts_6_image1','');
INSERT INTO `wp_postmeta` VALUES (2361,280,'layouts_7_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2362,280,'layouts_7_text1','Power Drive :\r\nInstantly switches between assist mode and cruise mode, allowing the user to receive assistance only when needed, which help maintain the user’s remaining physical function.');
INSERT INTO `wp_postmeta` VALUES (2363,280,'layouts_8_layout_type','l-image-2column:画像2カラム ■■');
INSERT INTO `wp_postmeta` VALUES (2364,280,'layouts_8_image1','');
INSERT INTO `wp_postmeta` VALUES (2365,280,'layouts_8_image2','565');
INSERT INTO `wp_postmeta` VALUES (2366,280,'layouts_8_image3','85');
INSERT INTO `wp_postmeta` VALUES (2367,280,'layouts_8_image4','86');
INSERT INTO `wp_postmeta` VALUES (2368,280,'layouts_9_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2369,280,'layouts_9_embed','https://www.youtube.com/embed/Hu4-uC9_-Ls');
INSERT INTO `wp_postmeta` VALUES (2370,280,'clients_partners','1');
INSERT INTO `wp_postmeta` VALUES (2371,280,'layouts_0_text1','The wheelchair, Wheeliy, was jointly developed with Molten, a manufacturer of medical and welfare equipment, for active people with wheelchair userswho do not require a lot of assistance. quantum was in charge of all aspects of the project, from concept design, user research, hypothesis testing, design, and supporting design development for mass production.\r\n\r\nSince its launch in 2019, Wheeliy has received a lot of positive feedback and has been highly praised in international design awards such as reddot and iF\r\n\r\nThe Wheeliy Power Drive is a powered model that adds electric power assist to Wheeliy for further pursuit of the wellbeing of \"active wheelchair users”.');
INSERT INTO `wp_postmeta` VALUES (2372,280,'layouts_1_image1','264');
INSERT INTO `wp_postmeta` VALUES (2373,280,'layouts_3_text1','Wheeliy aims to provide unrestricted mobility for active users in society.\r\n\r\nWheeliy was developed by redefining wheelchairs as a means of transportation that also includes support from others, not just perfecting the performance of the product for personal use.\r\n\r\nThe wheelchair is designed with a color guide on the body that also allows those who are not familiar with wheelchairs to intuitively know where to hold the wheelchair to lift it stably and where to pull it up to fold, making it easier for others to support the user.');
INSERT INTO `wp_postmeta` VALUES (2374,280,'layouts_4_image1','267');
INSERT INTO `wp_postmeta` VALUES (2375,280,'layouts_6_text1','<b>Power Drive</b>: Instantly switches between assist mode and cruise mode, allowing the user to receive assistance only when needed, which help maintain the user’s remaining physical function.');
INSERT INTO `wp_postmeta` VALUES (2376,280,'layouts_7_image1','270');
INSERT INTO `wp_postmeta` VALUES (2377,280,'layouts_8_text1','<b>Power Cushion</b>: he internal air cell automatically adjusts air pressure to reduce pain in the hips and worries of bedsore during prolonged sitting.');
INSERT INTO `wp_postmeta` VALUES (2378,280,'layouts_9_image1','271');
INSERT INTO `wp_postmeta` VALUES (2379,280,'layouts_10_layout_type','l-image-2column:画像2カラム ■■');
INSERT INTO `wp_postmeta` VALUES (2380,280,'layouts_10_text1','<b>Bag-in-Bag</b>: he dual structure of the outer and inner bag allows you to easily carry the bag on your lap when loading and unloading.');
INSERT INTO `wp_postmeta` VALUES (2381,280,'layouts_11_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2382,280,'layouts_11_image1','272');
INSERT INTO `wp_postmeta` VALUES (2383,280,'layouts_12_layout_type','l-image-2column:画像2カラム ■■');
INSERT INTO `wp_postmeta` VALUES (2384,280,'layouts_12_text1','<b>Smart Phone Stand</b>: Allows the user to check the GPS navigation app on their smart phone to help them get around comfortably in new environments.');
INSERT INTO `wp_postmeta` VALUES (2385,280,'layouts_13_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2386,280,'layouts_13_image1','273');
INSERT INTO `wp_postmeta` VALUES (2387,280,'layouts_14_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2388,280,'layouts_14_text1','\r\nIn addition to functionality and aesthetics, we have thought deeply about the physical, mental, and social wellbeing of active wheelchair users in developing the product.\r\n\r\nWheelchair users, who lead independent lives in society, actively use their own power to maintain their residual functions and feel a sense of satisfaction from doing so. To support these values, Wheeliy is designed to be a self-propelled wheelchair, with assist functions available only when needed.');
INSERT INTO `wp_postmeta` VALUES (2389,280,'layouts_15_layout_type','l-image-2column:画像2カラム ■■');
INSERT INTO `wp_postmeta` VALUES (2390,280,'layouts_15_image1','274');
