INSERT INTO `wp_postmeta` VALUES (2393,280,'layouts_16_text1','Wheeliy Power Drive is in the permanent collection of the Pinakothek der Moderne in Munich, Germany, one of the largest museum of modern and contemporary art in Europe.');
