INSERT INTO `wp_postmeta` VALUES (2518,282,'layouts_1_text1','<b>Name/Logo</b>\r\nヒトの体温を感知して、触れたカラダをやさしく包みこんでくれる新素材であることから、HUman + therMO + FIT を掛け合わせて、「HUMOFIT®️」と名付けました。');
