INSERT INTO `wp_postmeta` VALUES (2542,282,'layouts_7_text1','<b>Brand Movie</b>\r\nHUMOFIT®️の持つ温度依存性、形状記憶性を直感的に伝えるためのムービー。開発の原点にある、「ヒトに寄り添う」という発想を込めて、老若男女全ての人の体にフィットする素材の魅力を表現しました。\r\n');
