INSERT INTO `wp_postmeta` VALUES (2615,293,'layouts_0_text1','quantum was in charge of the total branding of HUMOFIT®️, a new material developed by Mitsui Chemicals, Inc. that senses body temperature and gently cares for people.\r\n\r\nThe characteristics and appeal of the material are fully expressed through the naming, logo, website, video, and statement of the brand.\r\n\r\n');
