INSERT INTO `wp_postmeta` VALUES (2617,293,'layouts_1_text1','<b>Name/Logo</b>\r\n\r\nThe name \"HUMOFIT®️\" is a combination of the words \"HUman\", \"therMO\", and \"FIT\", as the new material senses human body temperature and gently wraps the body it touches.');
