INSERT INTO `wp_postmeta` VALUES (2621,293,'layouts_3_text1','<b>Typeface</b>\r\n\r\nHUMOFIT®️ has the characteristic of becoming soft when heated and hard when cooled. The HUMOFIT®️ logo typeface also reflects this dual material characteristic, expressing both hardness and softness in a single letter.');
