INSERT INTO `wp_postmeta` VALUES (2629,293,'layouts_7_text1','<b>Brand Video</b>\r\n\r\nThis video is designed to intuitively convey the temperature dependency and shape memory properties of HUMOFIT®️. We implemented the idea of “caring for people”, the origin of HUMOFIT®️.’s development, by expressing the appeal of the material that fits the body of all people, young and old, men and women.\r\n\r\n');
