INSERT INTO `wp_postmeta` VALUES (2961,312,'outline','Developing a silk skincare brand that nurtures bare skin while preserving Japan\'s sericulture industry and Kimono culture.');
INSERT INTO `wp_postmeta` VALUES (2962,312,'clients_partners','0');
INSERT INTO `wp_postmeta` VALUES (2963,312,'layouts_0_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2964,312,'layouts_0_text1','quantum has developed a silk skincare brand called \"QINUDE\" that contains natural silk fibroin extracted from Japanese raw silk. A rare silk which is said to be distributed only 1% in Japan.\r\n\r\n');
INSERT INTO `wp_postmeta` VALUES (2965,312,'layouts_1_layout_type','l-fullimage:画像ヨコイチ■');
INSERT INTO `wp_postmeta` VALUES (2966,312,'layouts_1_text1','日本国内の養蚕農家が激減し、素材から仕上げまですべて日本でつくられているきものが非常に少なくなっていることを受け立ち上げられた、純国産の絹ときもの文化を守る<a href=\"https://qinude.jp/project.html\" target=\"blank\">絹甦［けんこう］プロジェクト</a>をきっかけに開発をスタート。');
INSERT INTO `wp_postmeta` VALUES (2967,312,'layouts_2_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2968,312,'layouts_2_image1','303');
INSERT INTO `wp_postmeta` VALUES (2969,312,'layouts_3_layout_type','l-text-2column:テキストのみ1カラム □■');
