INSERT INTO `wp_postmeta` VALUES (2971,312,'layouts_3_image2','306');
INSERT INTO `wp_postmeta` VALUES (2972,312,'layouts','10');
INSERT INTO `wp_postmeta` VALUES (2973,312,'layouts_2_text1','quantum started the development of this brand in response to a project called Kenko.  The number of sericulture farmers in Japan and the number of Kimonos that are made entirely in Japan, from material to finish, has been drastically decreasing. Kenko was launched to preserve Japan’s domestic silk and Kimono culture.');
INSERT INTO `wp_postmeta` VALUES (2974,312,'layouts_4_layout_type','l-image1:画像のみ1カラム（通常サイズ）■□');
INSERT INTO `wp_postmeta` VALUES (2975,312,'layouts_4_text1','京友禅の老舗・千總のきものに使われているものと同じ生糸のフィブロインを加水分解した成分を全アイテムに含んでおり、日常的に純国産の絹を使うことで日本の養蚕業ときもの文化を支えながら、素肌を潤いある、健やかで美しい状態へと整えます。');
INSERT INTO `wp_postmeta` VALUES (2976,312,'layouts_5_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (2977,312,'layouts_5_image1','304');
