INSERT INTO `wp_postmeta` VALUES (3165,324,'layouts_0_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (3166,324,'layouts_0_text1','F. [éf], a flower bouquet brand that allows you to choose a bouquet from a story, launched its service on November 18th, 2019.');
INSERT INTO `wp_postmeta` VALUES (3167,324,'layouts_1_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (3168,324,'layouts_1_text1','F. [éf] is a brand launched with the aim of transforming bouquets, which is traditionally sensual and feminine, into something “more understandable” and “unisex”.\r\n\r\n');
INSERT INTO `wp_postmeta` VALUES (3169,324,'layouts_2_layout_type','l-image-2column:画像2カラム ■■');
INSERT INTO `wp_postmeta` VALUES (3170,324,'layouts_2_text1','花束を贈りたいけれど、どんなものを選べば相手に喜んでもらえるか感覚的にわからないという方のために、花束に込められた想いや花束のイメージを\"ストーリー\"という形で言語化することで、「これなら相手にマッチする、相手が喜んでくれる」という、納得感のある体験を提供します。\r\n');
INSERT INTO `wp_postmeta` VALUES (3171,324,'layouts_3_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (3172,324,'layouts_3_text1','Some people may feel that it is difficult to choose the perfect bouquet for someone but by verbalizing the message and the emotion of a bouquet in the form of a story, the user will be provided with a reassuring experience and feel confident that “the bouquet will match and delight whoever receives the bouquet”.\r\n');
