INSERT INTO `wp_postmeta` VALUES (3240,325,'_layouts_1_layout_type','field_60daf596a2cfb');
INSERT INTO `wp_postmeta` VALUES (3241,325,'layouts_1_text1','「MONET LABO」は、＜移動＞にまつわる社会課題の解決や新価値創造を企業間の連携により加速させるべく、企業横断型のコンソーシアムとして2019年に設立された「MONETコンソーシアム」の加盟企業に対して、MaaSの事業化を促進・支援するサービスをパッケージにして提供するプログラムです。\r\nビジネス開発からUX、PoC、システム開発まで様々なフェーズにおいて、プロフェッショナル・メンターとのマッチングやワークショップの提供などを加盟企業のニーズに合わせて実施、MaaS関連の事業開発を加速させます。\r\n');
