INSERT INTO `wp_postmeta` VALUES (3383,345,'_layouts_0_layout_type','field_60daf596a2cfb');
INSERT INTO `wp_postmeta` VALUES (3384,345,'layouts_0_text1','企業やブランドのもつ個性を音の資産として継承するBRANDED AUDIO STORAGE『SOUNDS GOOD®』を株式会社オトバンクと共同設立しました。\r\n\r\n2019年3月5日のローンチ以来、多くの企業やブランド、アーティストとコラボレーションし、音の資産を活用した新しいブランディングに取り組んでいます。');
INSERT INTO `wp_postmeta` VALUES (3385,345,'_layouts_0_text1','field_60db1bdce269b');
INSERT INTO `wp_postmeta` VALUES (3386,345,'layouts_1_layout_type','l-image1:画像のみ1カラム（通常サイズ）■□');
INSERT INTO `wp_postmeta` VALUES (3387,345,'_layouts_1_layout_type','field_60daf596a2cfb');
INSERT INTO `wp_postmeta` VALUES (3388,345,'layouts_1_text1','SOUNDS GOOD®は、企業の個性や象徴とも呼べる事業を、写真や映像に比べて想像を掻き立てやすい「余白」がある“音の資産”として残し、様々なクリエイターにその「余白」に魂を吹き込んでもらうことで、意味のある形で未来へと継承していくことを目指す事業です。');
INSERT INTO `wp_postmeta` VALUES (3389,345,'_layouts_1_text1','field_60db1bdce269b');
