INSERT INTO `wp_postmeta` VALUES (3392,345,'layouts_2_text1','SOUNDS GOOD®は、企業の個性や象徴とも呼べる事業を、写真や映像に比べて想像を掻き立てやすい「余白」がある“音の資産”として残し、様々なクリエイターにその「余白」に魂を吹き込んでもらうことで、意味のある形で未来へと継承していくことを目指す事業です。');
INSERT INTO `wp_postmeta` VALUES (3393,345,'_layouts_2_text1','field_60db1bdce269b');
