INSERT INTO `wp_postmeta` VALUES (3395,345,'_layouts_3_layout_type','field_60daf596a2cfb');
INSERT INTO `wp_postmeta` VALUES (3396,345,'layouts_3_text1','工場の製造ラインで発生する特徴的な音や、製品使用時の音といった“ブランドを象徴する音”を企業やブランドの“音の資産”として捉え、音楽アーティストやイラストレーター、写真家など様々なクリエイターたちとのコラボレーションから生まれるコンテンツとして提供。企業と生活者、消費者の新たな接点を生み出しています。これまであまり意識されてこなかった「音」を活用したブランディングの新しい形として、多くの企業、団体、メディアからご注目いただいています。');
