INSERT INTO `wp_postmeta` VALUES (3547,375,'layouts_0_text1','物理的な力が加わることで発電し、暗号化された通信ができるPanasonic AIS社（当時名称）の「ワイヤレスバッテリーレススイッチ」の技術に関連した新規事業を立ち上げる目的で、同社とquantumで共同プロジェクトを発足。このプロジェクトを通じて、バッテリーレススマートボタン『eny』（エニー）とコネクティッドキー『Jō』（ジョー）を企画、開発し、このうち『eny』は商品化・事業化に向け、パートナーとして併走しました。\r\n');
