INSERT INTO `wp_postmeta` VALUES (4009,423,'outline','Joint development of IoT signage that senses the weather.');
INSERT INTO `wp_postmeta` VALUES (4010,423,'clients_partners','0');
INSERT INTO `wp_postmeta` VALUES (4011,423,'layouts_0_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4012,423,'layouts_0_text1','“QM weather.\", <feel the weather with IoT signage> acquires and displays weather information from the Internet, has been jointly developed with artless Inc., KAMARQ HOLDINGS, and quantum.');
INSERT INTO `wp_postmeta` VALUES (4013,423,'layouts_1_layout_type','l-image1:画像のみ1カラム（通常サイズ）■□');
INSERT INTO `wp_postmeta` VALUES (4014,423,'layouts_1_text1','毎日をより快適に過ごすために必要な「天気予報」。その繰り返しチェックする情報を、いちいちスマートフォンを起動して確かめることなく、より直感的に、そしてより心地よく感じることができる、ひとつのアートピースのようなインテリアへと昇華させました。');
INSERT INTO `wp_postmeta` VALUES (4015,423,'layouts_2_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4016,423,'layouts_2_text1','“Weather forecasts” are essential for making everyday life more comfortable but you may need to check your smartphone regularly for the latest information. However, “QM weather” eliminates the need to do so by integrating the weather information as an art piece-like interior design, thus making it an intuitive and comfortable process.');
INSERT INTO `wp_postmeta` VALUES (4017,423,'layouts_3_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4018,423,'layouts_3_text1','“QM weather” has a simple structure with only four icons: sunny, cloudy, rainy, and snowy, and displays the current weather by lighting up and the forecast weather by blinking. The color of the light indicates the forecast temperature.');
INSERT INTO `wp_postmeta` VALUES (4019,423,'layouts_4_layout_type','l-fullimage:画像ヨコイチ■');
INSERT INTO `wp_postmeta` VALUES (4020,423,'layouts_4_text1','QM、artless Inc.、京源、DIGITAL HABITSという国内外のデザイナーによる４種のオリジナルデザインに加え、NTTドコモの絵文字が2016年にMoMAコレクションに収蔵されたことを記念して開発された「emoji ウェザーフォーキャスター」や、2018年のミラノサローネに出展した、土台部分にスワロフスキー®・クリスタルを使用したデザイン、代官山に本店を構える手ぬぐいメーカー「かまわぬ」のアイコンデザインなど、企業やブランドとのコラボレーションも実施しています。');
INSERT INTO `wp_postmeta` VALUES (4021,423,'layouts_5_layout_type','l-fullimage:画像ヨコイチ■');
INSERT INTO `wp_postmeta` VALUES (4022,423,'layouts_5_text1','RED DOT DESIGN AWARD2018受賞のほか、数多くのAWARDで高い評価を得ています。');
INSERT INTO `wp_postmeta` VALUES (4023,423,'layouts_6_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4024,423,'layouts_6_text1','The product will be released gradually from July 2017, and is currently available at Tsutaya Books and KAMARQ\'s online store.');
