INSERT INTO `wp_postmeta` VALUES (4035,423,'layouts_8_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4036,423,'layouts_8_text1','In addition to four original designs by Japanese and international designers - QM, artless Inc., Kyogen, and DIGITAL HABITS - the “Emoji Weather Forecaster” was developed to celebrate the addition of NTT DOCOMO\'s emoji in the MoMA collection in 2016. We have also collaborated with companies and brands, such as icon designs by Kamawanu, a Tenugui (hand towel) maker with its head office in Daikanyama, and Swarovski®, where Swarovski® crystals were used on the base, which were both exhibited at the Salone del Mobile Milano 2018.');
INSERT INTO `wp_postmeta` VALUES (4037,423,'layouts_9_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4038,423,'layouts_9_text1','In addition to winning the RED DOT DESIGN AWARD 2018, the company has received high acclaim from numerous other awards.');
