INSERT INTO `wp_postmeta` VALUES (4305,445,'outline','Supporting the development and growth of NTT DOCOMO’s automatic translation application.');
INSERT INTO `wp_postmeta` VALUES (4306,445,'clients_partners','1');
INSERT INTO `wp_postmeta` VALUES (4307,445,'layouts_0_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4308,445,'layouts_0_text1','NTT DOCOMO\'s \"Hanashite Honyaku®\" is an automatic translation application that allows users to translate spoken words into each other\'s native language via smartphones and other devices.');
INSERT INTO `wp_postmeta` VALUES (4309,445,'layouts_1_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4310,445,'layouts_1_text1','quantum is supporting the development and growth of this service, including its trial support.');
INSERT INTO `wp_postmeta` VALUES (4311,445,'layouts_2_layout_type','l-image1:画像のみ1カラム（通常サイズ）■□');
INSERT INTO `wp_postmeta` VALUES (4312,445,'layouts_2_image1','544');
INSERT INTO `wp_postmeta` VALUES (4313,445,'layouts_3_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4314,445,'layouts_3_text1','“Hanashite Honyaku®\" is an application that allows users to translate spoken words into each other\'s native language through smartphones and other devices with the application installed. By using a smartphone or tablet, the application provides various functions, such as \"face-to-face translation,\" which allows users to talk with a non-Japanese speaker in person, and \"Utsushite Honyaku,\" which translates non-Japanese words captured by a camera into Japanese and displays it on the screen.');
INSERT INTO `wp_postmeta` VALUES (4315,445,'layouts_4_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4316,445,'layouts_4_text1','The \"Phone Translation,\" which started its free trial for corporate users on September 7th, 2020, is a function that translates the voice of a speaker, during a call and allows the user to converse with the caller in their native language. When users receive an incoming call from a non-Japanese speaker on a smartphone that has the \"Hanashite Honyaku®\" application installed, it will enable the user to communicate with the automatically translated language. (Supported languages are English, Chinese, and Korean.)');
INSERT INTO `wp_postmeta` VALUES (4317,445,'layouts_5_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4318,445,'layouts_5_text1','Since the caller (non-Japanese speaker) does not need to install any application to use the translation function, it supports smooth multilingual communication in a variety of situations such as responding to inquiries from non-Japanese speaking customers and teleconferences with overseas parties.');
INSERT INTO `wp_postmeta` VALUES (4319,445,'layouts_6_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4320,445,'layouts_6_text1','With the spread of new lifestyles due to COVID-19, the application has been developed for business use in various industries such as restaurants, lodging, manufacturing, hospitals, municipalities, and passenger services.');
INSERT INTO `wp_postmeta` VALUES (4321,445,'layouts_7_layout_type','l-text-2column:テキストのみ1カラム □■');
INSERT INTO `wp_postmeta` VALUES (4322,445,'layouts_7_text1','For more information on the optional telephone translation service of \"Hanashite Honyaku\", please refer to the following\r\n<a href=\"https://honyaku.idc.nttdocomo.co.jp/biz/phonetranslation\" target=\"blank\">https://honyaku.idc.nttdocomo.co.jp/biz/phonetranslation</a>\r\n\r\n<small>“Hanashite Honyaku®” and the “Hanashite Honyaku®” logo are registered trademarks of NTT DOCOMO, INC.</small>\r\n\r\n\r\n');
