INSERT INTO `wp_postmeta` VALUES (5872,280,'layouts_5_text1','The Wheeliy Power Drive is a model that adds an electric power assist to the Wheeliy, helping the user travel farther by switching to the power drive mode as needed on roads that are rough or uphill.\r\n\r\nIn addition to the electricalassist, various attachments are available to relieve the difficulties of travelling and comprehensively support the desire of wheelchair users to go outside.');
